import React, { useState } from 'react';
import { getFilms, getGenres } from './api';
import './App.scss';
import { FilmList, Header, MemoSelect } from './components';
import { FilmsResponse, GenresState } from './app.types';

function App() {
  const [films, setFilms] = useState<FilmsResponse>();
const [genres, setGenres] = useState<GenresState>()
  React.useEffect(() => {
    getFilms('', setFilms);
  }, []);


  const [options, setOptions] = useState<{ id: number; name: string }[]>([]);
  React.useEffect(() => {
    getGenres('', setOptions);
  }, []);

  const handleSelectOption = (id: number) => {
    getFilms(`&with_genres=${id}`, setFilms);
  };

  console.log(films);

  return (
    <div className="app">
      <Header />
      <div className="content">
        <MemoSelect handleSelectOption={handleSelectOption} />
        <FilmList filmList={films?.results} />
      </div>
    </div>
  );
}

export default App;
