import React, { FC, useCallback, useMemo, useState } from 'react';
import { getGenres } from '../../api';
import { GenresState } from '../../app.types';
import './select.scss';
import { SelectProps } from './select.types';

const Select: FC<SelectProps> = ({ handleSelectOption }) => {
  const [isOpenSelect, setIsOpenSelect] = useState(false);
  const [selectedOptions, setSelectedOptions] = useState<GenresState>([]);
  const color = useMemo(() => 'CFB8B4', [selectedOptions]);

  const handleOpenSelect = useCallback(() => {
    setIsOpenSelect(!isOpenSelect);
  }, [isOpenSelect]);

  console.log({ options });
  console.log({ selectedOptions });

  return (
    <div className="selectWrapper">
      <div className="select">
        {selectedOptions?.[0]?.id ? (
          selectedOptions?.map(({ id, name }) => {
            return (
              <div
                key={id}
                style={{ backgroundColor: `#${color}49` }}
                className="chosenOptionsWrapper"
                onClick={() => {
                  setSelectedOptions((prev) =>
                    prev?.filter((option) => option.id !== id)
                  );
                }}
              >
                <span className="chosenOptionName">{name}</span>
                <div className="deleteChosenOption">+</div>
              </div>
            );
          })
        ) : (
          <p className="emptyValue">Choose some category</p>
        )}
        <div className="toggleOpenSelect" onClick={() => handleOpenSelect()}>
          &#x27BE;
        </div>
      </div>
      {isOpenSelect && (
        <div className="optionsWrapper">
          {/* <div onClick={() => handleOpenSelect()} className="option">
            option
          </div> */}
          {options
            ?.filter(
              (option) =>
                !selectedOptions?.some((sOption) => sOption.id === option.id)
            )
            .map(({ id, name }) => (
              <div
                key={id}
                onClick={() => {
                  handleSelectOption(id);
                  setSelectedOptions((prev: { id: number; name: string }[]) => [
                    ...prev,
                    { id, name },
                  ]);
                  handleOpenSelect();
                }}
                className="option"
              >
                {name}
              </div>
            ))}
        </div>
      )}
    </div>
  );
};

export const MemoSelect = React.memo(Select);
