import React, { FC, useState } from 'react';
import { PaginationProps } from './pagition.types';

export const Pagination: FC<PaginationProps> = ({ count }) => {
  const [selectedTab, setSelectedTab] = useState(1);

  return (
    <div className="paginationWrapper">
      <div onClick={() => setSelectedTab(selectedTab + 1)} className="first">1</div>
      {[
        selectedTab - 2 > 1 ? selectedTab - 2 : '',
        selectedTab - 1 > 1 ? selectedTab - 1 : '',
        selectedTab,
        selectedTab + 1 < count ? selectedTab + 1 : '',
        selectedTab + 2 < count ? selectedTab + 2 : '',
      ].map((item) => (
        <div onClick={() => {
          if (typeof item === 'string') {
            return;
          }
          setSelectedTab(item);
        }} className="tab">{item}</div>
      ))}
      <div className="last">{count}</div>
    </div>
  );
};
