import React, { FC, useCallback, useMemo, useState } from 'react';
import { getGenres } from '../../api';
import './select.scss';
import { SelectProps } from './select.types';

export const Select: FC<SelectProps> = ({ handleSelectOption }) => {
  const [isOpenSelect, setIsOpenSelect] = useState(false);
  const [selectedOptions, setSelectedOptions] = useState();
  const [options, setOptions] = useState<{ id: number; name: string }[]>([]);
  const color = useMemo(
    () => Math.floor(Math.random() * 16777215).toString(16),
    [selectedOptions]
  );

  const handleOpenSelect = useCallback(() => {
    setIsOpenSelect(!isOpenSelect);
  }, [isOpenSelect]);

  React.useEffect(() => {
    getGenres('', setOptions);
  }, []);

  console.log({ options });

  return (
    <div className="selectWrapper">
      <div className="select">
        {options?.[0]?.id ? (
          options?.map(({ id, name }) => {
            return (
              <div
                key={id}
                style={{ backgroundColor: `#${color}49` }}
                className="chosenOptionsWrapper"
              >
                <span className="chosenOptionName">{name}</span>
                <div className="deleteChosenOption">+</div>
              </div>
            );
          })
        ) : (
          <p className="emptyValue">Choose some category</p>
        )}
        <div
          onClick={() => {
            handleOpenSelect();
            setSelectedOptions({id, name})
          }}
          className="toggleOpenSelect"
        >
          &#x27BE;
        </div>
      </div>
      {isOpenSelect && (
        <div className="optionsWrapper">
          {/* <div onClick={() => handleOpenSelect()} className="option">
            option
          </div> */}
          {options?.map(({ id, name }) => (
            <div
              key={id}
              onClick={() => handleSelectOption(id)}
              className="option"
            >
              {name}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
