import React, { FC, useState } from 'react';
import { PaginationProps } from './pagination.types';
import './pagination.scss';

export const Pagination: FC<PaginationProps> = ({ count }) => {
  const [selectedTab, setSelectedTab] = useState<number>(1);

  return (
    <div className="paginationWrapper">
      {
        /* selectedTab !== 1 */ true && (
          <div onClick={() => setSelectedTab(1)} className="first">
            1
          </div>
        )
      }
      <div className="dynamicTabsWrapper">
        {[
          selectedTab - 2 > 1 ? selectedTab - 2 : null,
          selectedTab - 1 > 1 ? selectedTab - 1 : null,
          selectedTab === 1 || selectedTab === count ? null : selectedTab,
          selectedTab + 1 < count ? selectedTab + 1 : null,
          selectedTab + 2 < count ? selectedTab + 2 : null,
        ].map((item, id, array) => {
          return (
            item && (
              <div
                onClick={() => {
                  if (typeof item === 'string') {
                    return;
                  }
                  setSelectedTab(item);
                }}
                className="tab"
              >
                {item}
              </div>
            )
          );
          id < array.length ? null : null;
        })}
      </div>
      {/* selectedTab !== count */ true && <div className="last">{count}</div>}
    </div>
  );
};
