import React, { useState } from 'react';
import { isArray } from 'util';
import { getFilms, getGenres } from './api';
import { SortList } from './app.enums';
import { FilmsResponse, GenresState, HandleSearchPropsType } from './app.types';

const useAppData = () => {
  const [films, setFilms] = useState<FilmsResponse>();
  const [genres, setGenres] = useState<GenresState>([]);
  const [queryParams, setQueryParams] = useState<{
    [key: string]: string | number;
  }>({
    genresIds: '',
    sortValue: '',
    page: '',
  });
  React.useEffect(() => {
    getFilms('', setFilms);
  }, []);

  React.useEffect(() => {
    getGenres('', setGenres);
  }, []);

  const handleSelectOption = (ids: number[]) => {
    const convertedIds = ids.join(',');
    getFilms(`&with_genres=${convertedIds}`, setFilms);
  };

  const handleSort = (sortValue: SortList) => {
    getFilms(`&sort_by=${sortValue}`, setFilms);
  };

  const handlePagination = (page: number) => {
    getFilms(`&page=${Math.ceil(page / 2)}`, setFilms);
  };

  const handleSearch = async ({
    genresIds,
    sortValue,
    page,
  }: HandleSearchPropsType) => {
    setQueryParams({
      ...queryParams,
      genresIds: genresIds?.join(',') ?? queryParams.genresIds,
      sortValue: sortValue ?? queryParams.sortValue,
      page: page ?? queryParams.page,
    });

    console.log(Object.keys(queryParams));

    const queryParamsString = [queryParams].map((query) => {
      if (isArray(query)) {
        return `${[query]}=${query.join(',')}`;
      }

      return `${[query]}=${query}`;
    });

    console.log(queryParamsString);
    getFilms(queryParamsString?.join('&'), setFilms);
  };

  return {
    films,
    setFilms,
    genres,
    setGenres,
    handleSelectOption,
    handleSort,
    handlePagination,
    handleSearch,
  };
};

export { useAppData };
