import React from 'react';
import { useAppData } from './app.hook';
import './App.scss';
import { FilmList, Header, MemoSelect } from './components';
import { Sort } from './components/sort';

function App() {
  const { films, genres, handleSelectOption, handleSort } = useAppData();

  return (
    <div className="app">
      <Header />
      <div className="content">
        <div className="contentTop">
          <MemoSelect
            options={genres}
            handleSelectOption={handleSelectOption}
          />
          <Sort handleSort={handleSort} />
        </div>
        <FilmList genres={genres} filmList={films?.results} />
      </div>
    </div>
  );
}

export default App;
