import React, { useState } from 'react';
import { getFilms, getGenres } from './api';
import './App.scss';
import { FilmsResponse, GenresState } from './app.types';
import { FilmList, Header, MemoSelect } from './components';
import { Sort } from './components/sort';

function App() {
  const [films, setFilms] = useState<FilmsResponse>();
  const [genres, setGenres] = useState<GenresState>([]);
  React.useEffect(() => {
    getFilms('', setFilms);
  }, []);

  React.useEffect(() => {
    getGenres('', setGenres);
  }, []);

  const handleSelectOption = (ids: number[]) => {
    const convertedIds = ids.join(',');
    getFilms(`&with_genres=${convertedIds}`, setFilms);
  };

  return (
    <div className="app">
      <Header />
      <div className="content">
        <MemoSelect options={genres} handleSelectOption={handleSelectOption} />
        <Sort />
        <FilmList genres={genres} filmList={films?.results} />
      </div>
    </div>
  );
}

export default App;
