import React, { FC, RefObject, useCallback, useEffect, useRef, useState } from "react";
import "./select.scss";

const useClickAway = (
  ref: RefObject<HTMLDivElement>,
  handleClickOutside: () => void,
  dependency: boolean
) => {

  const handleClick = (event: any) => {
    if (ref.current && !ref.current.contains(event.target) && dependency) {
      handleClickOutside();
    }
  };

  useEffect(() => {
    // Bind the event listener
    document.addEventListener("mousedown", handleClick);
    return () => {
      // Unbind the event listener on clean up
      document.removeEventListener("mousedown", handleClick);
    };
  });
};

export const Select: FC = () => {
  const [isOpenSelect, setIsOpenSelect] = useState(false);

  const handleOpenSelect = useCallback(() => {
    setIsOpenSelect(!isOpenSelect);
  }, [isOpenSelect]);

  const ref = useRef<HTMLDivElement>() as React.MutableRefObject<HTMLInputElement>

  useClickAway(ref, () => handleOpenSelect(), !isOpenSelect)

  return (
    <div className="wrapper">
      <div className="select" onClick={() => handleOpenSelect()}>
        {[1, 2, 3, 4].map((item) => {
          const randomColor = Math.floor(Math.random() * 16777215).toString(16);
          return (
            <div
              key={item}
              style={{ backgroundColor: `#${randomColor}49` }}
              className="chosenOptionsWrapper"
            >
              <span className="chosenOptionName">someName</span>
              <div className="deleteChosenOption">+</div>
            </div>
          );
        })}
      </div>
      {isOpenSelect && (
        <div className="optionsWrapper">
          <div className="option">option</div>
          <div className="option">option</div>
          <div className="option">option</div>
          <div className="option">option</div>
          <div className="option">option</div>
