import axios from 'axios';

const BASE_URL = 'https://api.themoviedb.org/3/discover/movie';

const getFilms = (url: string) => {
    axios
    .get(`${BASE_URL}/${url}`)
    .then((resolve) => resolve)
    .catch();
};

export { getFilms };
