import classnames from 'classnames';
import React, { FC, useState } from 'react';
import './pagination.scss';
import { PaginationProps } from './pagination.types';

export const Pagination: FC<PaginationProps> = ({
  count,
  handlePagination,
}) => {
  const [selectedTab, setSelectedTab] = useState<number>(1);

  return (
    <>
      {count && (
        <div className="paginationWrapper">
          <div
            onClick={() => {
              if (selectedTab !== 1) {
                setSelectedTab(1);
                handlePagination({
                  page: 1,
                  isFetch:
                    (1 + 1 === selectedTab),
                });
              }
            }}
            /* eslint-disable-next-line */
            className={classnames('first', { ['selected']: selectedTab === 1 })}
            >
            1
          </div>
          <div className="dynamicTabsWrapper">
            {[
              selectedTab + 1 > count ? selectedTab - 5 : null,
              selectedTab + 2 > count ? selectedTab - 4 : null,
              selectedTab + 3 > count ? selectedTab - 3 : null,
              selectedTab - 2 > 1 ? selectedTab - 2 : null,
              selectedTab - 1 > 1 ? selectedTab - 1 : null,
              selectedTab === 1 || selectedTab === count ? null : selectedTab,
              selectedTab + 1 < count ? selectedTab + 1 : null,
              selectedTab + 2 < count ? selectedTab + 2 : null,
              selectedTab - 2 < 1 ? selectedTab + 3 : null,
              selectedTab - 3 < 1 ? selectedTab + 4 : null,
              // selectedTab - 4 < 1 ? selectedTab + 5 : null,
            ].map((item) => {
              return (
                <>
                  {item && (
                    <div
                      onClick={() => {
                        if (typeof item === 'string') {
                          return;
                        }
                        if (selectedTab !== item) {
                          handlePagination({
                            page: item,
                            isFetch:
                              (item % 2 === 0 && item - 1 === selectedTab)
                              || (item % 2 !== 0 && item + 1 === selectedTab),
                          });
                          setSelectedTab(item);
                        }
                      }}
                      /* eslint-disable-next-line */
                      className={classnames('tab', { ['selected']: selectedTab === item })}
                    >
                      {item}
                    </div>
                  )}
                </>
              );
            })}
          </div>
          <div
            onClick={() => {
              if (selectedTab !== count) {
                handlePagination({
                  page: count,
                  isFetch:
                    !((count % 2 === 0 && count - 1 === selectedTab)
                    || (count % 2 !== 0 && count + 1 === selectedTab)),
                });
                setSelectedTab(count);
              }
            }}

            /* eslint-disable-next-line */
            className={classnames('last', { ['selected']: selectedTab === count })}
            >
            {count}
          </div>
        </div>
      )}
    </>
  );
};
