import React, { useState } from 'react';
import { getFilms } from './api';
import './App.scss';
import { FilmList, Header, Select } from './components';

function App() {
  const [state, setstate] = useState<any>();
  React.useEffect(() => {
    getFilms('', setstate);
    // setstate(response);
  }, []);

  const handleSelectOption = (id: number) => {
    getFilms(`&with_genres=${id}`);
  };

  console.log(state);

  return (
    <div className="app">
      <Header />
      <div className="content">
        <Select handleSelectOption={handleSelectOption} />
        <FilmList filmList={[{ id: 1 }, { id: 2 }, { id: 3 }]} />
      </div>
    </div>
  );
}

export default App;
