import React, { useState } from 'react';
import { getFilms } from './api';
import './App.scss';
import { FilmList, Header, MemoSelect } from './components';
import { FilmsResponse } from './components/app.types';

function App() {
  const [films, setFilms] = useState<FilmsResponse>();

  React.useEffect(() => {
    getFilms('', setFilms);
  }, []);

  const handleSelectOption = (id: number) => {
    getFilms(`&with_genres=${id}`, setFilms);
  };

  console.log(films);

  return (
    <div className="app">
      <Header />
      <div className="content">
        <MemoSelect handleSelectOption={handleSelectOption} />
        <FilmList filmList={films} />
      </div>
    </div>
  );
}

export default App;
