import React, { useCallback, useMemo, useState } from 'react';
import './sort.scss';

enum SortList {
  SORT_ASC = 'release_date.asc',
  SORT_DESC = 'release_date.desc',
}

export const Sort = () => {
  const [isOpenSelect, setIsOpenSelect] = useState(false);
  const [sortBy, setSortBy] = useState('');
  const handleOpenSelect = useCallback(() => {
    setIsOpenSelect(!isOpenSelect);
  }, [isOpenSelect]);

  const sort = useMemo(
    () => [
      { id: SortList.SORT_ASC, name: 'By newest' },
      { id: SortList.SORT_DESC, name: 'By oldest' },
    ],
    input
  );

  const handleSelect = (sortValue: string) => {
    setSortBy(sortValue);
  };

  return (
    <div className="sortWrapper">
      <div className="select">
        {sortBy ? (
          <span className="chosenOptionName">{sortBy}</span>
        ) : (
          <span className="chosenOptionName">Sort</span>
        )}

        <div className="toggleOpenSelect" onClick={() => handleOpenSelect()}>
          &#x27BE;
        </div>
      </div>
      {isOpenSelect && (
        <div className="optionsWrapper">
          {[1, 2].map((item) => (
            <div
              key={item}
              onClick={() => {
                handleOpenSelect();
                handleSelect(String(item));
              }}
              className="option"
            >
              name
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
