import axios from 'axios';

const API_KEY = '55603383b6e7d248eaf4b2df9f74c6ec';
const BASE_URL = `https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-US`;

const api = axios.create({
  baseURL: BASE_URL,
  responseType: 'json',
});

const getFilms = async (url: string, setState: (re: any) => void) => {
  return api
    .get(`${url}`, {
      params: {},
    })
    .then((response) => setState(response.data));
};

export { getFilms };
