import React, { useState } from 'react';
import { isArray } from 'util';
import { getFilms, getGenres } from './api';
import { SortList } from './app.enums';
import { FilmsResponse, GenresState } from './app.types';

const useAppData = () => {
  const [films, setFilms] = useState<FilmsResponse>();
  const [genres, setGenres] = useState<GenresState>([]);

  React.useEffect(() => {
    getFilms('', setFilms);
  }, []);

  React.useEffect(() => {
    getGenres('', setGenres);
  }, []);

  const handleSelectOption = (ids: number[]) => {
    const convertedIds = ids.join(',');
    getFilms(`&with_genres=${convertedIds}`, setFilms);
  };

  const handleSort = (sortValue: SortList) => {
    getFilms(`&sort_by=${sortValue}`, setFilms);
  };

  const handlePagination = (page: number) => {
    getFilms(`&page=${Math.ceil(page / 2)}`, setFilms);
  };

  const handleSearch = ({
    genresIds,
    sortValue,
    page,
  }: {
    genresIds: number[];
    sortValue: SortList;
    page: number;
  }) => {
    const queryParams = [
      { with_genres: genresIds },
      { sort_by: sortValue },
      { page },
    ];

    return () => {
      const queryParamsString = `${queryParams.map((item) => {
        if (isArray(item)) {
          return `&${[item]}=${item.join(',')}`;
        }

        return `${[item]}=${item}`;
      })}`;

      getFilms(queryParamsString, setFilms);
    };
  };

  return {
    films,
    setFilms,
    genres,
    setGenres,
    handleSelectOption,
    handleSort,
    handlePagination,
  };
};

export { useAppData };
