import axios from 'axios';

const API_KEY = '55603383b6e7d248eaf4b2df9f74c6ec';
const BASE_URL = 'https://api.themoviedb.org/3';
const GENRES_URL = `${BASE_URL}//genre/movie/list?api_key=${API_KEY}&language=en-US`;
const MOVIE_URL = `${BASE_URL}/discover/movie?api_key=${API_KEY}&language=en-US`;

const getMovies = axios.create({
  baseURL: MOVIE_URL,
  responseType: 'json',
});

const getGenresHTTP = axios.create({
  baseURL: GENRES_URL,
  responseType: 'json',
});

const getGenres = async (setState: (re: any) => void) => {
  return getGenresHTTP.then((response: any) => setState(response.data.genres));
};

const getFilms = async (url: string, setState: (re: any) => void) => {
  return getMovies
    .get(`${url}`, {
      params: {},
    })
    .then((response) => setState(response.data));
};

export { getFilms, getGenres };
