import React, { FC } from 'react';
import { BASE_URL } from '../../api';
import './film-card.scss';
import { FilmCardProps } from './film-card.types';

export const FilmCard: FC<FilmCardProps> = ({
  image,
  genreIds,
  title,
  year,
}) => {
  return (
    <div className="filmCardWrapper">
      <img src={`${BASE_URL}/${image}`} alt={title} className="image" />
      <div className="contentWrapper">
        <div className="contentTop">
          <h4 className="title">{title}</h4>
          <p className="year">{year}</p>
        </div>
        <div className="genresWrapper">
          {genreIds.map((item, id, array) => (
            <p key={item} className="genre">
              {id !== array.length - 1 ? 'genre, ' : 'genre'}
            </p>
          ))}
        </div>
      </div>
    </div>
  );
};
