import React, { useState } from 'react';
import { getFilms, getGenres } from './api';
import { SortList } from './app.enums';
import './App.scss';
import { FilmsResponse, GenresState } from './app.types';
import { FilmList, Header, MemoSelect } from './components';
import { Sort } from './components/sort';

function App() {
  const [films, setFilms] = useState<FilmsResponse>();
  const [genres, setGenres] = useState<GenresState>([]);

  React.useEffect(() => {
    getFilms('', setFilms);
  }, []);

  React.useEffect(() => {
    getGenres('', setGenres);
  }, []);

  const handleSelectOption = (ids: number[]) => {
    const convertedIds = ids.join(',');
    getFilms(`&with_genres=${convertedIds}`, setFilms);
  };

  const handleSort = (sortValue: SortList) => {
    getFilms(`&sort_by=${sortValue}`, setFilms);
  };

  return (
    <div className="app">
      <Header />
      <div className="content">
        <div className="contentTop">
          <MemoSelect
            options={genres}
            handleSelectOption={handleSelectOption}
          />
          <Sort handleSort={handleSort} />
        </div>
        <FilmList genres={genres} filmList={films?.results} />
      </div>
    </div>
  );
}

export default App;
